defined( 'ABSPATH' ) || exit();

use WeCodeArt\Markup\SVG;
use function WeCodeArt\Functions\get_prop;

$attributes = isset( $attributes ) ? $attributes : [];
$classnames = [ 'wp-block-post-author', 'wp-block-post-author--wide' ];

if( $value = get_prop( $attributes, 'textAlign', false ) ) {
    $classnames[] = 'has-text-align-' . $value;
}

if( $value = get_prop( $attributes, 'className', false ) ) {
    $classnames[] = $value;
}

?>
<div class="<?php echo esc_attr( implode( ' ', $classnames ) ); ?>">
	<h3 class="wp-block-post-author__headline"><?php
			
		SVG::render( 'user' );
		
		?><span style="margin-left:0.5em;"><?php
		
			if( $byline = get_prop( $attributes, 'byline', false ) ) {

				echo wp_kses_post( $byline );

			} else {
				printf( esc_html__( 'About %s', 'wecodeart' ), sprintf(
					'<a href="%s" rel="author">%s</a>',
					$author->url,
					$author->name
				) );
			}

		?></span>
	</h3>
	<div class="row gx-2 gx-lg-3">
		<div class="wp-block-post-author__avatar col-auto"><?php

			echo get_avatar(
				$author->id,
				get_prop( $attributes, 'avatarSize', 90 ), 
				'',
				sprintf( esc_html__( '%s`s avatar', 'wecodeart' ), $author->name )
			);

		?></div>
		<div class="wp-block-post-author__description col"><?php
		
			echo wp_kses_post( $author->description );
			
			if( isset( $social ) && is_array( $social ) ) {
				$classnames = [ 'wp-block-post-author__social', 'row', 'gx-2' ];

				if( $value = get_prop( $attributes, 'textAlign', false ) ) {
					$classnames[] = 'justify-content-' . $value;
				}
			?>
			<div class="<?php echo esc_attr( implode( ' ', $classnames ) ); ?>"><?php
				foreach( $social as $item ) {
					$key = strtolower( $item['title'] );
					$classes = [ 'wp-block-post-author__social-item', 'wp-block-post-author__social-item--' . $key, 'col-auto' ];
				?>
				<a class="<?php echo esc_attr( implode( ' ', $classes ) ); ?>"
					href="<?php echo esc_url( $item['url'] ); ?>"
					target="<?php echo esc_attr( $item['target'] ); ?>"
					rel="<?php echo ( $item['target'] === '_self' ) ? 'follow' : 'nofollow' ; ?>"><?php 
					
					SVG::render( $item['icon'] );

				?></a>
				<?php } ?>
			</div>
			<?php } ?>
		</div>
	</div>
</div>