defined( 'ABSPATH' ) || exit();

use function WeCodeArt\Functions\get_prop;

$attributes = isset( $attributes ) ? $attributes : [];
$classnames = [ 'wp-block-post-author' ];

if( $value = get_prop( $attributes, 'textAlign', false ) ) {
    $classnames[] = 'has-text-align-' . $value;
}

if( $value = get_prop( $attributes, 'className', false ) ) {
    $classnames[] = $value;
}

?>
<div class="<?php echo esc_attr( implode( ' ', $classnames ) ); ?>">
	<h3 class="wp-block-post-author__headline"><?php
			
		wecodeart( 'markup' )->SVG::render( 'user' );
		
		?><span><?php
		
			if( $byline = get_prop( $attributes, 'byline', esc_html__( 'About', 'wecodeart' ) ) ) {

				printf( '%s %s', $byline, sprintf( '<a href="%s" rel="author">%s</a>', $author->url, $author->name ) );

			}

		?></span>
	</h3>
	<div class="grid">
		<div class="wp-block-post-author__avatar g-col-3 g-col-sm-2"><?php

			echo get_avatar(
				$author->id,
				get_prop( $attributes, 'avatarSize', 90 ), 
				'',
				sprintf( esc_html__( '%s`s avatar', 'wecodeart' ), $author->name )
			);

		?></div>
		<div class="wp-block-post-author__description g-col-9 g-col-sm-10"><?php
		
			echo wp_kses_post( $author->description );
			
			if( isset( $social ) && ! empty( $social ) ) {
				echo $social;
			}

			?>
		</div>
	</div>
</div>