namespace WeCodeArt\Admin\Customizer\Configs;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Admin\Customizer\Config;
use WeCodeArt\Admin\Customizer\Formatting;

/**
 * Customizer Config Footer setup
 */
class Footer extends Config {
	/**
	 * Register Site Layout Customizer Configurations
	 *
	 * @param 	array                $configurations 
	 * @param 	WP_Customize_Manager $wp_customize instance of WP_Customize_Manager.
	 * @since 	3.6
	 *
	 * @return 	array 
	 */
	public function register( $configurations, $wp_customize ) {
		$_configs = [
			// Copyright
			[
				'name'			=> 'footer-copyright-text',
				'type'        	=> 'control',
				'control'  		=> 'textarea',
				'section'		=> 'footer-copyright',
				'title'			=> esc_html__( 'Footer Copyright Text', 'wecodeart' ),
				'description'	=> __( 'You can use <code>[copy]</code> and <code>[year]</code> to output copyright symbol and current year.', 'wecodeart' ),
				'priority'		=> 5,
				'sanitize_callback'	=> 'sanitize_text_field', 
				'transport'		=> 'postMessage',
				'partial'		=> [
					'selector'        => '.wp-site-credits__text',
					'render_callback' => [ 'WeCodeArt\Admin\Customizer\Partials', 'render_footer_copyright' ]
				],
				'output'		=> [
					[
						'element'  	=> '.wp-site-credits__text',
						'function' 	=> 'html',
						'text'		=> true
					]
				]
			],
			// Design
			[
				'name'			=> 'footer-design-attribution_bg',
				'type' 			=> 'control',
				'control'  		=> 'wecodeart-color',
				'section'		=> 'footer-design',
				'title' 		=> esc_html__( 'Attribution background color', 'wecodeart' ),
				'description'	=> esc_html__( 'Background color of the footer credits wrapper. Defaults to CSS color.', 'wecodeart' ),
				'transport'		=> 'postMessage',
				'output'		=> [
					[
						'element'  => '.wp-site-credits',
						'property' => 'background-color'
					],
					[
						'element'  => '.wp-site-credits',
						'property' => 'border-top',
						'pattern'  => function( $value ) {
							$pattern = '1px solid $';
							
							if( wecodeart_if( 'with_styles' ) ) {
								$adjusted 	= wecodeart( 'integrations' )->get( 'styles' )::hex_brightness( $value, -20 );
								$pattern	= '1px solid ' . $adjusted;
							}
							
							return $pattern;
						},
					]
				]
			],
			[
				'name'			=> 'footer-design-attribution_color',
				'type' 			=> 'control',
				'control'  		=> 'wecodeart-color',
				'section'		=> 'footer-design',
				'title' 		=> esc_html__( 'Attribution text color', 'wecodeart' ),
				'description'	=> esc_html__( 'Defaults to CSS color.', 'wecodeart' ),
				'transport'		=> 'postMessage',
				'output'		=> [
					[
						'element'  => '.wp-site-credits',
						'property' => 'color'
					]
				]
			],
			[
				'name'			=> 'footer-design-attribution_links',
				'type' 			=> 'control',
				'control'  		=> 'wecodeart-color',
				'section'		=> 'footer-design',
				'title' 		=> esc_html__( 'Attribution links color', 'wecodeart' ),
				'description'	=> esc_html__( 'Defaults to CSS color.', 'wecodeart' ),
				'transport'		=> 'postMessage',
				'output'		=> [
					[
						'element'  => '.wp-site-credits a',
						'property' => 'color'
					]
				]
			]
		];

		return array_merge( $configurations, $_configs );
	}
}
