namespace WeCodeArt\Admin\Customizer\Configs;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Admin\Customizer\Config;
use WeCodeArt\Admin\Customizer\Formatting;

/**
 * Customizer Config Performance setup
 */
class Performance extends Config {
	/**
	 * Register Site Layout Customizer Configurations
	 *
	 * @param 	array                $configurations 
	 * @param 	WP_Customize_Manager $wp_customize instance of WP_Customize_Manager.
	 * @since 	5.0.0
	 *
	 * @return 	array 
	 */
	public function register( $configurations, $wp_customize ) {
		$_configs = [
			[
				'name'			=> 'general-performance-head',
				'type' 			=> 'control',
				'control'  		=> 'checkbox',
				'section'		=> 'general-performance',
				'title' 		=> esc_html__( 'Clean Head', 'wecodeart' ),
				'description' 	=> esc_html__( 'Check this box if you want to clean the header of stuff like Emoji, REST links, Feeds etc.', 'wecodeart' ),
				'priority' 		=> 5,
				'transport'		=> 'postMessage',
				'sanitize_callback'	=> [ Formatting::get_instance(), 'sanitize_checkbox' ]
			],
		];

		return array_merge( $configurations, $_configs );
	}
}
