namespace WeCodeArt\Admin\Customizer\Configs;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Admin\Customizer\Config;

/**
 * Customizer Config Typography setup
 */
class Typography extends Config {
	/**
	 * Register Site Layout Customizer Configurations
	 *
	 * @param 	array                $configurations 
	 * @param 	WP_Customize_Manager $wp_customize instance of WP_Customize_Manager.
	 * @since 	5.0.0
	 *
	 * @return 	array 
	 */
	public function register( $configurations, $wp_customize ) {
		$_configs = [
			[
				'name'			=> 'general-typography-primary',
				'type' 			=> 'control',
				'control'  		=> 'wecodeart-fonts',
				'section'		=> 'general-typography',
				'title' 		=> esc_html__( 'Primary Font', 'wecodeart' ),
				'description' 	=> esc_html__( 'This font is used on body and it will be placed in Boostrap`s sans serif CSS variable.', 'wecodeart' ),
				'priority' 		=> 5,
				'transport'		=> 'postMessage',
				'output'		=> [
					[
						'element'  	=> ':root',
						'property' 	=> '--wca-font-sans-serif',
					]
				]
			],
			[
				'name'			=> 'general-typography-secondary',
				'type' 			=> 'control',
				'control'  		=> 'wecodeart-fonts',
				'section'		=> 'general-typography',
				'title' 		=> esc_html__( 'Secondary Font', 'wecodeart' ),
				'description' 	=> esc_html__( 'This font is used on headings and it will be placed in a custom CSS variable.', 'wecodeart' ),
				'priority' 		=> 5,
				'transport'		=> 'postMessage',
				'output'		=> [
					[
						'element'  	=> ':root',
						'property' 	=> '--wca-font-secondary',
					],
					[
						'element'  	=> 'h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6',
						'property' 	=> 'font-family',
						'pattern'  => function( $value ) {
							$value = sprintf( 'var(--wca-font-secondary, %s)', $value['font-family'] );
							return $value;
						},
					]
				]
			],
		];

		return array_merge( $configurations, $_configs );
	}
}
