namespace WeCodeArt\Admin\Customizer\Controls;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Core\Scripts;
use function WeCodeArt\Functions\get_prop;

/**
 * Sortable control (uses checkboxes).
 */
class Color extends \WP_Customize_Control {

	use Scripts\Base;

	/**
	 * The control type.
	 *
	 * @access 	public
	 * @var 	string
	 */
	public $type = 'wecodeart-color';
       
    /**
	 * Additional arguments passed to JS.
	 *
	 * @var string
	 */
    public $default = '';

	/**
	 * Enqueue control related scripts/styles.
	 *
	 * @access public
	 *
	 * @since 	unknown
	 * @version	5.0.0
	 */
	public function enqueue() {
		wp_enqueue_style(
			$this->make_handle(),
			$this->get_asset( 'css', 'customizer-color' ),
			[ 'wp-components' ],
			wecodeart( 'version' )
		);

		$deps = [ 'lodash', 'react', 'react-dom', 'wp-components', 'wp-element', 'wp-i18n', 'wp-polyfill' ];
		
		wp_enqueue_script(
            $this->make_handle(),
			$this->get_asset( 'js', 'customizer-color' ),
           	array_merge( $deps, [ 'jquery', 'customize-base' ] ),
			wecodeart( 'version' ),
            true
		);
    }
    
	/**
	 * Send to JS.
	 */
	public function to_json() {
		parent::to_json();
		$this->json['default'] = $this->setting->default;
		$this->json['inputAttrs'] = $this->input_attrs;
		
		$palette_theme 	= wecodeart_json( [ 'settings', 'color', 'palette', 'theme' ], [] );
		$palette_user 	= wecodeart_json( [ 'settings', 'color', 'palette', 'user' ], $palette_theme );

		$this->json['palette'] = $palette_user;
	}
}
