namespace WeCodeArt\Admin\Customizer\Controls;

defined( 'ABSPATH' ) || exit;

/**
 * Range Control
 */
class Range extends \WP_Customize_Control {

	use \WeCodeArt\Core\Scripts\Base;

	/**
	 * The control type.
	 *
	 * @access 	public
	 * @var 	string
	 */
	public $type = 'wecodeart-range';

	/**
	 * Enqueue control related scripts/styles.
	 *
	 * @access public
	 *
	 * @since 	unknown
	 * @version	4.2.0
	 */
	public function enqueue() {
		wp_enqueue_style(
			$this->make_handle(),
			$this->get_asset( 'css', 'customizer-range' ),
			[],
			wecodeart( 'version' )
		); 
		
		wp_enqueue_script(
            $this->make_handle(),
			$this->get_asset( 'js', 'customizer-range' ),
           	[ 'jquery', 'customize-base' ],
			wecodeart( 'version' ),
            true
		);

	}

	/**
	 * Additional arguments passed to JS.
	 *
	 * @var array
	 */
	public $input_attrs = [];

	/**
	 * Send to JS.
	 */
	public function to_json() {
		parent::to_json();
		$this->json['inputAttrs'] = $this->input_attrs;
	}
}
