namespace WeCodeArt\Admin\Customizer\Modules;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Singleton;
use WeCodeArt\Core\Scripts;
use WeCodeArt\Admin\Customizer;

/**
 * Customizer PostMessage
 */
class PostMessage {

	use Singleton;
	use Scripts\Base;

	/**
	 * Send to Constructor
	 * @since 3.6.2
	 */
	public function init() {
		add_action( 'customize_preview_init', [ $this, 'postmessage' ], 20 );
	}

	/**
	 * Add our Selective Refresh Partials.
	 * 
	 * @param  object $wp_customize An instance of the WP_Customize_Manager class.
	 */
	public function postmessage( $wp_customize ) {
		wp_enqueue_script(
			$this->make_handle(),
			$this->get_asset( 'js', 'customizer' ),
			[ 'jquery', 'customize-preview' ],
			wecodeart( 'version' ),
			true
		);

		$fields = Customizer::get_instance()->get_configurations( $wp_customize );
		$data   = [];
		foreach ( $fields as $field ) {
			$transport 	= isset( $field['transport'] ) && 'postMessage' === $field['transport'];
			$has_output = isset( $field['output'] ) && ! empty( $field['output'] ) && is_array( $field['output'] );
			if ( $transport && $has_output ) {
				$data[] = $field;
			}
		}
		wp_localize_script( $this->make_handle(), 'wecodeartPostMessageFields', $data );
		
		$extras = apply_filters( 'wecodeart/filter/customizer/postmessage', false );
		if ( $extras ) {
			wp_add_inline_script( $this->make_handle(), $extras, 'after' );
		}
	}
}