namespace WeCodeArt\Customizer\Configs;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Customizer\Config;
use WeCodeArt\Customizer\Formatting;

/**
 * Customizer Config Colors setup
 */
class Colors extends Config {
	/**
	 * Register Site Layout Customizer Configurations
	 *
	 * @param 	array                $configurations 
	 * @param 	WP_Customize_Manager $wp_customize instance of WP_Customize_Manager.
	 * @since 	4.2
	 *
	 * @return 	array 
	 */
	public function register( $configurations, $wp_customize ) {
		$_configs = [
			[
				'name'			=> 'general-colors-site',
				'type' 			=> 'control',
				'control'  		=> 'wecodeart-color',
				'section'		=> 'general-colors',
				'title' 		=> esc_html__( 'Site Background', 'wecodeart' ),
				'description' 	=> esc_html__( 'Choose site background color.', 'wecodeart' ),
				'transport'		=> 'postMessage'
			]
		];

		return array_merge( $configurations, $_configs );
	}
}
