/**
 * Customizer Configs Panel/Sections
 */
class Configs extends Config {
	/**
	 * Register Site Layout Customizer Configurations.
	 * @param 	Array                $configurations 
	 * @param 	WP_Customize_Manager $wp_customize instance of WP_Customize_Manager.
	 * @since 	3.5
	 * @return 	Array 
	 */
	public function register( $configurations, $wp_customize ) {
		$configs = array(

			/**
			 * Header 
			 */
			array(
				'name'     => 'header',
				'type'     => 'panel',
				'priority' => 5,
				'title'    => __( 'Header Settings', 'wecodeart' ) 
			),
			array(
				'name'     => 'header-bar',
				'type'     => 'section',
				'priority' => 5,
				'title'    => __( 'Header Bar', 'wecodeart' ),
				'panel'    => 'header',
			),
			/**
			 * Content
			 */
			array(
				'name'     => 'content',
				'type'     => 'panel',
				'priority' => 10,
				'title'    => __( 'Content Settings', 'wecodeart' ) 
			),
			array(
				'name'     => 'content-layout',
				'type'     => 'section',
				'priority' => 5,
				'title'    => __( 'Layout Settings', 'wecodeart' ),
				'panel'    => 'content',
			),
			/**
			 * Footer
			 */
			array(
				'name'     => 'footer',
				'type'     => 'panel',
				'priority' => 15,
				'title'    => __( 'Footer Settings', 'wecodeart' ) 
			),
			array(
				'name'     => 'footer-layout',
				'type'     => 'section',
				'priority' => 5,
				'title'    => __( 'Layout Settings', 'wecodeart' ),
				'panel'    => 'footer',
			),
			array(
				'name'     => 'footer-copyright',
				'type'     => 'section',
				'priority' => 10,
				'title'    => __( 'Copyright', 'wecodeart' ),
				'panel'    => 'footer',
			)
		);

		return array_merge( $configurations, $configs );
	}
}
