namespace WeCodeArt\Customizer\Controls;

defined( 'ABSPATH' ) || exit;

use WP_Customize_Control;

if ( class_exists( 'WeCodeArt\Customizer\Controls\Color' ) ) return NULL;

/**
 * Sortable control (uses checkboxes).
 */
class Color extends WP_Customize_Control {

	use \WeCodeArt\Core\Scripts\Base;

	/**
	 * The control type.
	 *
	 * @access 	public
	 * @var 	string
	 */
	public $type = 'wecodeart-color';
       
    /**
	 * Additional arguments passed to JS.
	 *
	 * @var string
	 */
    public $default = '';
    
	/**
	 * Disable Alpha in colorpicker.
	 *
	 * @var bool
	 */
    public $disable_alpha = false;

	/**
	 * Enqueue control related scripts/styles.
	 *
	 * @access public
	 *
	 * @since 	unknown
	 * @version	4.2.0
	 */
	public function enqueue() {
		wp_enqueue_style(
			$this->make_handle(),
			$this->get_asset( 'css', 'customizer-color' ),
			[ 'wp-components' ],
			wecodeart( 'version' )
		);

		$deps = [ 'lodash', 'react', 'react-dom', 'wp-components', 'wp-element', 'wp-i18n', 'wp-polyfill' ];
		
		wp_enqueue_script(
            $this->make_handle(),
			$this->get_asset( 'js', 'customizer-color' ),
           	array_merge( $deps, [ 'jquery', 'customize-base' ] ),
			wecodeart( 'version' ),
            true
		);

		wp_localize_script( $this->make_handle(), 'wecodeartColorControl', [
			'pallete' 	=> current( get_theme_support( 'editor-color-palette' ) )
		] );
    }
    
	/**
	 * Send to JS.
	 */
	public function to_json() {
		parent::to_json();
		$this->json['default']      = $this->default;
		$this->json['disableAlpha'] = $this->disable_alpha;
	}
}
