namespace WeCodeArt\Customizer\Controls;

if ( ! defined( 'ABSPATH' ) ) exit;

use WP_Customize_Control;

if ( class_exists( 'WeCodeArt\Customizer\Controls\Divider' ) ) return NULL;

/**
 * Simple divider control that only displays some content.
 */
class Divider extends WP_Customize_Control {
	/**
	 * The control type.
	 *
	 * @access  public
	 * @var     string
	 */
	public $type = 'wecodeart-divider';

	/**
	 * The control type.
	 *
	 * @access  public
	 * @var     string
	 */
	public $caption = '';

	/**
	 * The control type.
	 *
	 * @access public
	 * @var string
	 */
	public $separator = true;

	/**
	 * Enqueue control related scripts/styles.
	 *
	 * @access public
	 */
	public function enqueue() {
		$handle = strtolower( str_replace( '\\', '-', __CLASS__ ) );
		wp_enqueue_style( $handle, get_theme_file_uri( '/assets/css/customizer/controls/divider.css' ), null );
	}

	/**
	 * Refresh the parameters passed to the JavaScript via JSON.
	 *
	 * @see WP_Customize_Control::to_json()
	 */
	public function to_json() {
		parent::to_json();
		$this->json['label']       = esc_html( $this->label );
		$this->json['caption']     = $this->caption;
		$this->json['description'] = $this->description;
		$this->json['separator']   = $this->separator;
	}

	/**
	 * An Underscore (JS) template for this control's content (but not its container).
	 *
	 * Class variables for this control class are available in the `data` JS object;
	 * export custom variables by overriding {@see WP_Customize_Control::to_json()}.
	 *
	 * @see WP_Customize_Control::print_template()
	 *
	 * @access protected
	 */
	protected function content_template() {
		?>

		<# if ( data.caption ) { #>
			<span class="customize-control-caption">{{{ data.caption }}}</span>
		<# } #>

		<# if ( data.separator == true || data.separator.top == true ) { #>
			<hr />
		<# } #>

		<div class="customize-control-wecodeart-divider__wrapper">
			<label class="customizer-text">
				<# if ( data.label ) { #>
					<span class="customize-control-title">{{{ data.label }}}</span>
				<# } #>
				<# if ( data.description ) { #>
					<span class="description customize-control-description">{{{ data.description }}}</span>
				<# } #>
			</label>
		</div>

		<# if ( data.separator == true || data.separator.bottom == true ) { #>
			<hr />
		<# } #>

		<?php
	}
}
