namespace WeCodeArt\Customizer\Controls;

defined( 'ABSPATH' ) || exit;

use WP_Customize_Control;
use WeCodeArt\Admin\Request;
use WeCodeArt\Support\Fonts as The_Fonts;
use function WeCodeArt\Core\Scripts\get_asset;

if ( class_exists( 'WeCodeArt\Customizer\Controls\Fonts' ) ) return NULL;

/**
 * Sortable control (uses checkboxes).
 */
class Fonts extends WP_Customize_Control {

	use \WeCodeArt\Core\Scripts\Base;

	/**
	 * The control type.
	 *
	 * @access 	public
	 * @var 	string
	 */
	public $type = 'wecodeart-fonts';

	/**
	 * Additional arguments passed to JS.
	 *
	 * @var array
	 */
	public $input_attrs = [];

	/**
	 * Enqueue control related scripts/styles.
	 *
	 * @access public
	 *
	 * @since 	unknown
	 * @version	4.2.0
	 */
	public function enqueue() {
		wp_enqueue_style(
			$this->make_handle(),
			$this->get_asset( 'css', 'customizer-fonts' ),
			[ 'wp-components' ],
			wecodeart( 'version' )
		);

		$deps = [ 'lodash', 'react', 'react-dom', 'wp-components', 'wp-element', 'wp-i18n', 'wp-polyfill' ];
		
		wp_enqueue_script(
            $this->make_handle(),
			$this->get_asset( 'js', 'customizer-fonts' ),
           	array_merge( $deps, [ 'jquery', 'customize-base' ] ),
			wecodeart( 'version' ),
            true
		);

		wp_localize_script( $this->make_handle(), 'wecodeartFontsControl', [
			'typekit' 	=> [],
			'fonts' 	=> [
				'System' => The_Fonts::get_standard_fonts(),
				'Google' => The_Fonts::get_google_fonts(),
			]
		] );

		wp_enqueue_style(
			$this->make_handle( 'fonts' ),
			add_query_arg( [
				'family' 	=> implode( '|', wp_list_pluck( The_Fonts::get_google_fonts(), 'family' ) ),
				'text'		=> 'Abc',
				'display' 	=> 'swap',
			], '//fonts.googleapis.com/css' ),
			[],
			wecodeart( 'version' )
		);
	}

	/**
	 * Get standard fonts
	 *
	 * @return array
	 */
	public function get_standard_fonts() {
		return apply_filters( 'wecodeart/filter/customizer/control/fonts/standard', [
			[
				'family' => 'Arial, Helvetica, sans-serif'
			],
			[
				'family' => 'Arial Black, Gadget, sans-serif'
			],
			[
				'family' => 'Bookman Old Style, serif'
			],
			[
				'family' => 'Comic Sans MS, cursive'
			],
			[
				'family' => 'Courier, monospace'
			],
			[
				'family' => 'Georgia, serif'
			],
			[
				'family' => 'Garamond, serif'
			],
			[
				'family' => 'Impact, Charcoal, sans-serif'
			],
			[
				'family' => 'Lucida Console, Monaco, monospace'
			],  
			[
				'family' => 'Lucida Sans Unicode, Lucida Grande, sans-serif'
			],  
			[
				'family' => 'MS Sans Serif, Geneva, sans-serif'
			],  
			[
				'family' => 'Palatino Linotype, Book Antiqua, Palatino, serif'
			],  
			[
				'family' => 'Tahoma, Geneva, sans-serif'
			],  
			[
				'family' => 'Times New Roman, Times, serif'
			],
			[
				'family' => 'Trebuchet MS, Helvetica, sans-serif'
			],
			[
				'family' => 'Verdana, Geneva, sans-serif'
			],
			[
				'family' => 'Paratina Linotype'
			],
			[
				'family' => 'Trebuchet MS'
			],
		] );
	}

	/**
	 * Send to JS.
	 */
	public function to_json() {
		parent::to_json();
		$this->json['inputAttrs'] = is_array( $this->input_attrs ) ? wp_json_encode( $this->input_attrs ) : $this->input_attrs;
	}
}
