namespace WeCodeArt\Gutenberg\Blocks;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Markup;
use WeCodeArt\Markup\SVG;
use WeCodeArt\Singleton;
use function WeCodeArt\Functions\get_prop;

/**
 * Gutenberg File block.
 */
class File extends Dynamic {

	use Singleton;

	/**
	 * Shortcircuit Register
	 */
	public function register_block_type() {
		add_filter( 'render_block', [ $this, 'filter_render' ], 10, 2 );
	}

	/**
	 * Filter file markup
	 *
	 * @param	string 	$block_content
	 * @param	array 	$block
	 */
	public function filter_render( $block_content, $block ) {		
		if ( 'core/file' !== $block['blockName'] ) {
			return $block_content;
		}

		return $this->render( get_prop( $block, 'attrs', [] ), $block_content );
	}

	/**
	 * Dynamically renders the `core/file` block.
	 *
	 * @param 	array 	$attributes The block attributes.
	 *
	 * @return 	string 	The block markup.
	 */
	public function render( $attributes = [], $content = '' ) {
		$exclude = [
			'btn-primary',
			'btn-secondary',
			'btn-success',
			'btn-danger',
			'btn-warning',
			'btn-light',
			'btn-dark',
			'btn-info',
		];
		
		$classes = explode( ' ', get_prop( $attributes, 'className', '' ) );

		$matched = array_intersect( $classes, $exclude );
		$ommited = array_diff( $classes, $exclude );

		$doc = $this->load_html( $content );

		// Wrapper Changes
		$wrapper	= $doc->getElementsByTagName( 'div' )->item(0);
		$wrapper->setAttribute( 'class', 'wp-block-file btn-group ' . join( ' ', $ommited ) );
		
		// Button Changes
		$links	= $wrapper->getElementsByTagName( 'a' );
		foreach( $links as $link ) {
			if( $link->getAttribute( 'class' ) === 'wp-block-file__button' ) {
				$btn_class  = [ 'wp-block-file__button', 'btn' ];
	
				if( ! empty( $matched ) ) {
					$btn_class = array_merge( $btn_class, $matched );
				}

				$link->setAttribute( 'class', join( ' ', $btn_class ) );
			} else {
				$link->setAttribute( 'class', join( ' ', [ 'wp-block-file__button', 'btn', 'btn-outline-primary' ] ) );
			}
		}

		return $doc->saveHTML();
	}
}
