namespace WeCodeArt\Gutenberg\Blocks\Comment;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Blocks\Dynamic;
use function WeCodeArt\Functions\get_prop;

/**
 * Gutenberg Pagination Numbers block.
 */
class Numbers extends Dynamic {

	use Singleton;

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $namespace = 'core';

	/**
	 * Block name.
	 *
	 * @var string
	 */
	protected $block_name = 'comments-pagination-numbers';

	/**
	 * Shortcircuit Register
	 */
	public function register() {
		add_filter( 'block_type_metadata_settings', [ $this, 'filter_render' ], 10, 2 );
	}

	/**
	 * Filter template markup
	 *
	 * @param	array 	$settings
	 * @param	array 	$data
	 */
	public function filter_render( $settings, $data ) {
		if ( $this->get_block_type() === $data['name'] ) {
			$settings = wp_parse_args( [
				'render_callback' => [ $this, 'render' ]
			], $settings );
		}
		
		return $settings;
	}

	/**
	 * Dynamically renders the `core/post-template` block.
	 *
	 * @param 	array 	$attributes The block attributes.
	 *
	 * @return 	string 	The block markup.
	 */
	public function render( $attributes = [], $content = '', $block = null ) {
		

		return $content;
	}

	/**
	 * Render Comments Pagination - not working yet but it will be implemented.
	 *
	 * @since 	5.2.2
	 * @version 5.3.1
	 *
	 * @return 	string|null
	 */
	public function get_pagination() {
		/**
		 * Early Break
		 */
		if( empty( get_previous_comments_link() || get_next_comments_link() ) ) return;

		return wecodeart( 'markup' )::wrap( 'wp-block-comments-nav', [
			[
                'tag'   => 'nav',
                'attrs' => [
					'class' 		=> 'wp-block-comments-query-loop__pagination',
					'aria-label'    => esc_html__( 'Comments Navigation', 'wecodeart' ),
                ]
            ],
			[
                'tag'   => 'div',
                'attrs' => [
                    'class' => 'row pb-3'
                ]
            ]
		], function() {
			?>
            <h3 class="screen-reader-text"><?php esc_html_e( 'Comments Navigation', 'wecodeart' ); ?></h3>
			<?php

			wecodeart( 'markup' )::wrap( 'comments-prev-link', [ [
				'tag' 	=> 'div',
				'attrs' => [ 
					'class' => 'col-sm-12 col-md'
				] 
			] ], 'previous_comments_link' );
	
			wecodeart( 'markup' )::wrap( 'comments-next-link', [ [
				'tag' 	=> 'div',
				'attrs' => [
					'class' => 'col-sm-12 col-md text-md-end'
				] 
			] ], 'next_comments_link' );
		}, [], false );
    }

	/**
	 * Block styles
	 *
	 * @return 	string 	The block styles.
	 */
	public function styles() {
		return "
		.comments__add-new {
			margin: .5rem 0;
		}
		.wp-block-comments-query-loop:empty {
			display: none;
		}
		.wp-block-comments-query-loop__head svg {
			margin-right: .5rem;
		}
		";
	}
}
