namespace WeCodeArt\Gutenberg\Blocks\Comment;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Blocks\Dynamic;
use function WeCodeArt\Functions\get_prop;

/**
 * Gutenberg Post Template block.
 */
class Template extends Dynamic {

	use Singleton;

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $namespace = 'core';

	/**
	 * Block name.
	 *
	 * @var string
	 */
	protected $block_name = 'comment-template';

	/**
	 * Shortcircuit Register
	 */
	public function register() {
		add_filter( 'block_type_metadata_settings', [ $this, 'filter_render' 	], 10, 2 );
	}

	/**
	 * Filter template markup
	 *
	 * @param	array 	$settings
	 * @param	array 	$data
	 */
	public function filter_render( $settings, $data ) {
		if ( $this->get_block_type() === $data['name'] ) {
			$settings = wp_parse_args( [
				'render_callback' => [ $this, 'render' ]
			], $settings );
		}
		
		return $settings;
	}

	/**
	 * Dynamically renders the `core/comment-template` block.
	 *
	 * @param 	array 	$attributes The block attributes.
	 *
	 * @return 	string 	The block markup.
	 */
	public function render( $attributes = [], $content = '', $block = null ) {
		$post_id = $block->context['postId'];

        // Bail out early if the post ID is not set for some reason.
        if ( ! isset( $post_id ) ) {
            return '';
        }

		if( post_password_required( $post_id ) ) {
			return wecodeart( 'markup' )::wrap( 'wp-block-comments-protected', [ [
				'tag' 	=> 'p',
			] ], 'printf', [ 
				esc_html__( 'This post is password protected. Enter the password to view comments or leave a comment.', 'wecodeart' )
			], false );
		}

		$number = $block->context['queryPerPage'];

        // Get an array of comments for the current post.
        $comments = get_approved_comments( $post_id, [
			'hierarchical' 	=> get_option( 'thread_comments' ) ? 'threaded' : false,
			'number' 		=> $number,
		] );

		// Required Utilities
		wecodeart( 'styles' )->Utilities->load( [ 'ps-3', 'ps-md-5', 'mt-5', 'mb-5', 'my-1' ] );

		// Content
		$content = '';

		// Head
		$content .= $this->get_head();

		// List
        $content .= wecodeart( 'markup' )::wrap( 'wp-block-comments-list', [
			[
				'tag' 	=> 'ul',
				'attrs'	=> [
					'id'	=> 'comments',
					'class' => 'wp-block-comments-query-loop__list list-unstyled'
				]
			]
		], [ $this, 'render_comments' ], [ $comments, $block ], false );

		return $content;
	}

	/**
	 * Render Threaded Comments
	 *
	 * @since	5.3.3
	 * @version	5.3.3
	 *
	 * @return 	string
	 */
	public function render_comments( $comments, $block ) {
		$content = '';

		foreach ( $comments as $comment ) {
	
			$block_content = ( new \WP_Block( $block->parsed_block, [
				'commentId' => $comment->comment_ID,
			] ) )->render( [ 'dynamic' => false ] );
	
			$children = $comment->get_children();
	
			// If the comment has children, recurse to create the HTML for the nested comments.
			if ( ! empty( $children ) ) {
				$block_content .= wecodeart( 'markup' )::wrap( 'wp-block-comments-query-children', [
					[
						'tag' 	=> 'ol',
						'attrs'	=> [
							'class' => 'wp-block-comments-query-loop__children ps-3 ps-md-5 mt-5 list-unstyled'
						]
					]
				], [ $this, 'render_comments' ], [ $children, $block ], false );
			}
	
			$content .= wecodeart( 'markup' )::wrap( 'wp-block-comment', [
				[
					'tag' 	=> 'li',
					'attrs'	=> [
						'id'	=> 'comment-' . $comment->comment_ID,
						'class' => implode( ' ', get_comment_class( 'mb-5', $comment ) )
					]
				]
			], $block_content, [], false );
		}
	
		// This is ok, we render gutenberg blocks here.
		echo $content;
	}

	/**
	 * Render Comments Info
	 *
	 * @since	5.2.2
	 * @version	5.3.3
	 *
	 * @return 	string
	 */
	public function get_head() {
		$defaults = [
			'icon' 		=> wecodeart( 'markup' )->SVG::compile( 'comments' ), // Escaped with kses inside fn.
			'empty' 	=> esc_html__( 'No comments', 'wecodeart' ),
			'closed'	=> false,
			'add_one'	=> esc_html__( 'add one', 'wecodeart' )
		]; 

		$args = apply_filters( 'wecodeart/filter/comments/head/args', $defaults, get_post_type() );

		$icon 	= '';
		$head 	= '';

		$allow_comments		= comments_open();
		$comments_number 	= intval( get_comments_number() );
		
		if( $allow_comments ) {
			$icon	= (string) $args['icon'];
			$head 	= (string) $args['empty'];
		} else if( $args['closed'] !== false && is_string( $args['closed'] ) ) {
			$icon 	= (string) $args['icon'];
			$head 	= $args['closed'];
		}

		if ( 0 !== $comments_number ) {
			$head = sprintf(
				_nx( '%1$s comment', '%1$s comments', $comments_number, 'comments title', 'wecodeart' ),
				number_format_i18n( $comments_number )
			);
		}

		// Prepare HTML output
		$output = trim( sprintf( '%1$s %2$s', $icon, $head ) );

		// Append `add comment` link
		if( $allow_comments ) {
			$output .= sprintf(
				'<a class="comments__add-new float-end my-1 has-small-font-size" href="#respond" rel="nofollow">%s</a>',
				(string) $args['add_one']
			); 
		}

		return wecodeart( 'markup' )::wrap( 'wp-block-comments-head', [ [
			'tag' 	=> 'h3',
			'attrs' => [
				'class' => 'wp-block-comments-query-loop__head'
			]
		] ], $output, [], false );
	}

	/**
	 * Block styles
	 *
	 * @return 	string 	The block styles.
	 */
	public function styles() {
		return "
		.wp-block-comments-query-loop:empty {
			display: none;
		}
		.wp-block-comments-query-loop__head svg {
			margin-right: .5rem;
		}
		";
	}
}
