namespace WeCodeArt\Gutenberg\Blocks\Design;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Markup;
use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Blocks\Dynamic;
use function WeCodeArt\Functions\get_prop;

/**
 * Gutenberg Separator block.
 */
class Separator extends Dynamic {

	use Singleton;

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $namespace = 'core';

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $block_name = 'separator';

	/**
	 * Init
	 */
	public function init() {
		register_block_style( $this->get_block_type(), [
			'name' 	=> 'faded',
            'label'	=> __( 'Faded', 'wecodeart' ),
		] );
	}

	/**
	 * Shortcircuit Register
	 */
	public function register_block_type() {
		add_filter( 'render_block_core/separator', [ $this, 'render' ], 10, 2 );
	}

	/**
	 * Dynamically renders the `core/separator` block.
	 *
	 * @param 	string 	$content 	The block markup.
	 * @param 	array 	$block 		The parsed block.
	 *
	 * @return 	string 	The block markup.
	 */
	public function render( $content = '', $block = [], $data = null ) {
		$this->enqueue_styles();

		return $content;
	}

	/**
	 * Block styles
	 *
	 * @return 	string 	The block styles.
	 */
	public function styles( $attributes = [] ) {
		return '
		.wp-block-spacer {
			clear: both;
		}
		.wp-block-separator.is-style-faded {
			background: linear-gradient(to right, transparent, currentColor, transparent)!important;
			opacity: 1;
		}
		.wp-block-separator.is-style-wide {
			border-bottom-width: 1px;
		}
		.wp-block-separator.is-style-dots {
			background: none !important;
			border: none;
			text-align: center;
			max-width: none;
			line-height: 1;
			height: auto;
			opacity: 1;
		}
		.wp-block-separator.is-style-dots::before {
			content: "\00b7 \00b7 \00b7";
			color: currentColor;
			font-size: 20px;
			letter-spacing: 2em;
			padding-left: 2em;
			font-family: serif;
		}
		';
	}
}
