namespace WeCodeArt\Gutenberg\Blocks\Media;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Markup;
use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Blocks\Dynamic;
use function WeCodeArt\Functions\get_prop;

/**
 * Gutenberg File block.
 */
class File extends Dynamic {

	use Singleton;

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $namespace = 'core';

	/**
	 * Block name.
	 *
	 * @var string
	 */
	protected $block_name = 'file';

	/**
	 * Shortcircuit Register
	 */
	public function register() {
		add_filter( 'render_block_core/file', [ $this, 'render' ], 10, 2 );
	}

	/**
	 * Dynamically renders the `core/file` block.
	 *
	 * @param 	string 	$content 	The block markup.
	 * @param 	array 	$block 		The parsed block.
	 *
	 * @return 	string 	The block markup.
	 */
	public function render( $content = '', $block = [], $data = null ) {
		$exclude = [
			'bg-primary',
			'bg-secondary',
			'bg-success',
			'bg-danger',
			'bg-warning',
			'bg-light',
			'bg-dark',
			'bg-info',
			'text-primary',
			'text-secondary',
			'text-success',
			'text-danger',
			'text-warning',
			'text-light',
			'text-dark',
			'text-info',
		];

		$attributes = get_prop( $block, 'attrs', [] );

		$classes = explode( ' ', get_prop( $attributes, 'className', '' ) );

		$matched = array_intersect( $classes, $exclude );
		$ommited = array_diff( $classes, $exclude );

		$doc = $this->load_html( $content );

		// Wrapper Changes
		$wrapper	= $doc->getElementsByTagName( 'div' )->item(0);
		$classname 	= implode( ' ', array_merge( [ 'wp-block-file', 'd-flex', 'align-items-center' ], array_filter( $ommited ) ) );
		
		$wrapper->setAttribute( 'class', $classname );
		
		// Button Changes
		$links	= $wrapper->getElementsByTagName( 'a' );
		foreach( $links as $link ) {
			if( $link->getAttribute( 'class' ) === 'wp-block-file__button' ) {
				$btn_class  = [ 'wp-block-file__button', 'ms-3', 'py-1', 'px-3', 'rounded-pill' ];
	
				if( ! empty( $matched ) ) {
					$btn_class = array_merge( $btn_class, $matched );
				} else {
					$btn_class = array_merge( $btn_class, [ 'bg-dark', 'text-white' ] );
				}

				$link->setAttribute( 'class', join( ' ', $btn_class ) );
			} else {
				$link->setAttribute( 'class', join( ' ', [ 'wp-block-file__link' ] ) );
			}
		}

		return $this->save_html( $doc->saveHTML() );
	}
}
