namespace WeCodeArt\Gutenberg\Blocks\Text;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Markup;
use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Blocks\Dynamic;
use function WeCodeArt\Functions\get_prop;

/**
 * Gutenberg Code block.
 */
class Code extends Dynamic {

	use Singleton;

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $namespace = 'core';

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $block_name = 'code';

	/**
	 * Shortcircuit Register
	 */
	public function register_block_type() {
		add_filter( 'render_block_core/code', [ $this, 'render' ], 10, 2 );
	}

	/**
	 * Dynamically renders the `core/code` block.
	 *
	 * @param 	string 	$content 	The block markup.
	 * @param 	array 	$block 		The parsed block.
	 *
	 * @return 	string 	The block markup.
	 */
	public function render( $content = '', $block = [], $data = null ) {
		$this->enqueue_styles();

		return $content;
	}

	/**
	 * Block styles
	 *
	 * @return 	string 	The block styles.
	 */
	public function styles( $attributes = [] ) {
		return "
		.wp-block-code {
			padding: 1rem;
			border: 1px solid;
			color: var(--wp--preset--color--foreground, #212529);
			background-color: var(--wp--preset--color--white, white);
			border-color: var(--wp--preset--color--cyan-bluish-gray, #dee2e6);
			border-radius: var(--wca-border-radius, 0.25rem);
		}
		";
	}
}
