namespace WeCodeArt\Gutenberg\Blocks\Text;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Blocks\Dynamic;

/**
 * Gutenberg Code block.
 */
class Lists extends Dynamic {

	use Singleton;

	/**
	 * Block namespace.
	 *
	 * @var string
	 */
	protected $namespace = 'core';

	/**
	 * Block name.
	 *
	 * @var string
	 */
	protected $block_name = 'list';

	/**
	 * Shortcircuit Register
	 */
	public function register() {
		\add_filter( 'register_block_type_args',	[ $this, 'block_type_args'	], 20, 2 );
	}

	/**
	 * Block args
	 *
	 * @since	5.7.2
	 * @version	5.7.2
	 *
	 * @return 	array
	 */
	public function block_type_args( $args, $block_name ) {
		// List Block
		if ( $block_name === $this->get_block_type() ) {
			$args['supports']['spacing'] = [
				'margin'  	=> true,
				'padding' 	=> true,
				'blockGap' 	=> true,
			];
			$args['supports']['__experimentalLayout'] = [
				'allowSwitching'  => false,
				'allowInheriting' => false,
				'default'         => [
					'type'        => 'flex',
					'orientation' => 'vertical',
				],
			];
		}
		
		// List Item Block
		if ( $block_name === 'core/list-item' ) {
			$args['supports']['color'] = [
				'text'       => true,
				'background' => true,
				'link'       => true,
				'gradient'   => true,
			];
			$args['supports']['spacing'] = [
				'padding' => true,
				'margin'  => true,
			];
			$args['supports']['__experimentalBorder'] = [
				'radius'                        => true,
				'width'                         => true,
				'color'                         => true,
				'style'                         => true,
				'__experimentalDefaultControls' => [
					'width' => true,
					'color' => true,
				],
			];
		}

		return $args;
	}

	/**
	 * Block styles
	 *
	 * @return 	string 	The block styles.
	 */
	public function styles() {
		return "
			/* Reset */
			ol,
			ul {
				padding-left: 1.1em;
			}
			ol,
			ul,
			dl {
				margin-top: 0;
				margin-bottom: var(--wp--style--block-gap);
			}
			ol ol,
			ul ul,
			ol ul,
			ul ol {
				margin-bottom: 0;
			}
			dt {
				font-weight: 700;
			}
			dd {
				margin-bottom: .5rem;
				margin-left: 0;
			}
		";
	}
}
