namespace WeCodeArt\Gutenberg\Modules\Styles\Blocks;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Gutenberg\Modules\Styles\Blocks as Base;
use function WeCodeArt\Functions\get_prop;

/**
 * Block CSS Processor
 */
class Gallery extends Base {
	/**
	 * Parses an output and creates the styles array for it.
	 *
	 * @return 	null
	 */
	protected function process_extra() {
		$output				= [];
		$output['element'] 	= $this->element;

		// Columns
		if( $columns = get_prop( $this->attrs, 'columns', 3 ) ) {
			$this->output[] = wp_parse_args( [
				'property' 	=> '--wp--columns',
				'value'	  	=> $columns
			], $output );
		}

		// Gap
		if( $gap = get_prop( $this->attrs, [ 'style', 'spacing', 'blockGap' ] ) ) {
			if( count( $gap ) === 2 && count( array_unique( $gap ) ) === 1 ) {
				$gap = current( $gap );
			}

			if ( is_array( $gap ) ) {
				$gap_y	= get_prop( $gap, [ 'top' ] );
				$gap_x	= get_prop( $gap, [ 'left' ] );

				// Is WP way of saved color
				if( mb_strpos( $gap_x, 'var:preset|spacing' ) !== false ) {
					$gap_x = explode( '|', $gap_x );
					$gap_x = sprintf( 'var(--wp--preset--spacing--%s)', end( $gap_x ) );
				}

				// Is WP way of saved color
				if( mb_strpos( $gap_y, 'var:preset|spacing' ) !== false ) {
					$gap_y = explode( '|', $gap_y );
					$gap_y = sprintf( 'var(--wp--preset--spacing--%s)', end( $gap_y ) );
				}

				$gap = $gap_y . ' ' . $gap_x;
			} else {
				// Is WP way of saved color
				if( mb_strpos( $gap, 'var:preset|spacing' ) !== false ) {
					$gap = explode( '|', $gap );
					$gap = sprintf( 'var(--wp--preset--spacing--%s)', end( $gap ) );
				}
			}

			$this->output[] = wp_parse_args( [
				'property' 	=> '--wp--style--block-gap',
				'value'	  	=> $gap
			], $output );

			// Unset default gap
			$this->output[] = wp_parse_args( [
				'property' 	=> 'gap',
				'value'	  	=> null
			], $output );
		}
	}
}