namespace WeCodeArt\Gutenberg\Styles\Blocks;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Singleton;
use WeCodeArt\Gutenberg\Styles\Processor;
use function WeCodeArt\Functions\get_prop;

/**
 * Block CSS Processor
 */
class Spacer extends Processor {
	/**
	 * Parses an output and creates the styles array for it.
	 *
	 * @return 	void
	 */
	protected function process_style(): void {
		$declarations = [];
		
		// Height
		if ( $value = get_prop( $this->attrs, 'height', '100px' ) ) {
			$declarations['--wp--spacer-height'] = $value;
		}
		
		// Width
		if ( $value = get_prop( $this->attrs, 'width' ) ) {
			$declarations['--wp--spacer-width'] = $value;
		}

		if( ! empty( $declarations ) ) {
			$this->add_declarations( $declarations );
		}

		parent::process_style();
	}
}