namespace WeCodeArt\Markup\Input;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Markup;
use WeCodeArt\Markup\Input\Base;
use WeCodeArt\Markup\Input\Basic;
use function WeCodeArt\Functions\get_prop;

/**
 * Standard Inputs Markup
 */
class Toggle extends Base {

    /**
     * Input's Vars.
     *
     * @since   4.2.0
     * @var     string
     */
    public $label_position  = 'none';
    public $with_messages   = false;

    /**
	 * Constructor 
	 */
    public function __construct( string $type = 'toggle', array $args = [] ) {
        $type               = get_prop( $args, 'type', '' );
        $this->type         = in_array( $type, [ 'radio', 'checkbox' ] ) ? $type : 'radio';
        $this->unique_id    = wp_unique_id( 'toggle-' );
        $this->label        = get_prop( $args, 'label', '' );
        $this->attrs        = get_prop( $args, 'attrs', [] );
        $this->messages     = get_prop( $args, 'messages', [] );
    }

	/**
	 * Create HTML Inputs
	 *
	 * @since	unknown
	 * @version	4.2.0
	 */
	public function content() {  
        Markup::wrap( $this->type, [
            [
                'tag'   => 'div',
                'attrs' => [
                    'class' => implode( ' ', array_filter( [ 'form-check', $this->attrs['class'] ] ) ),
                ]
            ]
        ], function() {
            // Create a basic input
            $basic = new Basic( $this->type, [
                'label'     => $this->label,
                'attrs'     => wp_parse_args( [
                    'class' => 'form-check-input'
                ], $this->attrs ),
                'messages'  => $this->messages
            ] );

            // Change label position and unique ID
            $basic->label_position  = 'after';
            $basic->unique_id       = $this->unique_id;

            // Get the Input HTML
            echo $basic->get_content();
        } ); 
    }
}