namespace WeCodeArt;

defined( 'ABSPATH' ) || exit();

use ArrayAccess;

/**
 * Support for various plugins and features.
 */
class Support implements ArrayAccess {

	use Singleton; 

	/**
	 * The registered integrations.
	 *
	 * @var Integration[]
	 */
	protected $items = [];

	/**
	 * Send to Constructor
	 */
	public function init() {
		// Theme Support
		\add_action( 'after_setup_theme', [ $this, 'register_menus'		] );
		\add_action( 'after_setup_theme', [ $this, 'after_setup_theme'	] );
		\add_action( 'after_setup_theme', [ $this, 'load_translations'	] );

		// Register Default Integrations
		$this->register( 'extension/fonts',		Support\Fonts::class 		);
		$this->register( 'plugin/anr', 			Support\ANR::class 			);
		$this->register( 'plugin/wpseo', 		Support\WPSeo::class 		);
		$this->register( 'plugin/woocommerce', 	Support\WooCommerce::class 	);
	}

	/**
	 * Sets up theme defaults and registers support for various WordPress features.
	 *
	 * @since 	1.0
	 * @version	4.2
	 */
	public function after_setup_theme() {
		// Content width
		$GLOBALS['content_width'] = self::get_content_width();
		
		// Add support for Meta info for posts other than Page Type 
		add_post_type_support( 'post', 'wecodeart-post-info' );

		// Starter Content
		add_theme_support( 'starter-content', self::get_starter_content() );

		// Meta Modules
		add_theme_support( 'meta-modules', [ 'author', 'date', 'categories', 'tags', 'comments', 'edit' ] );

		// Automatic Feed Links
		add_theme_support( 'automatic-feed-links' );

		// Title Tag
		add_theme_support( 'title-tag' );

		// Selective refresh for widgets.
		add_theme_support( 'customize-selective-refresh-widgets' );

		// Thumbnails
		add_theme_support( 'post-thumbnails' );

		// Editor Style
		add_theme_support( 'editor-style' );

		// Custom Logo
		add_theme_support( 'custom-logo', [
			'height'      => 50,
			'width'       => 100,
		] );

		// HTML5
		add_theme_support( 'html5', [ 'gallery', 'caption', 'style', 'script', 'navigation-widgets' ] );

		// Responsive Embeds
		add_theme_support( 'responsive-embeds' );

		// AMP Support
		add_theme_support( 'amp', apply_filters( 'wecodeart/filter/support/amp', [ 
			'paired' => true 
		] ) );
	}

	/**
	 * Sets up theme content width.
	 *
	 * @since 	4.2.0
	 * @version	4.2.0
	 */
	public static function get_content_width() {
		$content_width = 840;

		if( wecodeart_if( 'is_full_layout' ) ) {
			$content_width = 1272;
		}

		return apply_filters( 'wecodeart/filter/content_width', $content_width );
	}

	/**
	 * Sets up theme starter content.
	 *
	 * @since 	4.0.2
	 * @version	4.0.2
	 */
	public static function get_starter_content() {
		// Define and register starter content to showcase the theme on new sites.
		$starter_content = array(
			'widgets'     => array(
				// Place one core-defined widgets in the first sidebar widget area.
				'primary' => array(
					'text_about',
				),
				// Place one core-defined widgets in the second footer widget areas.
				'footer-1' => array(
					'text_business_info',
				),
				'footer-2' => array(
					'text_business_info',
				),
				'footer-3' => array(
					'text_business_info',
				),
			),

			// Default to a static front page and assign the front and posts pages.
			'options'     => array(
				'show_on_front'  => 'page',
				'page_on_front'  => '{{front}}',
				'page_for_posts' => '{{blog}}',
			),

			// Set up nav menus for each of the two areas registered in the theme.
			'nav_menus'   => array(
				// Assign a menu to the "primary" location.
				'primary'  => array(
					'name'  => __( 'Primary', 'wecodeart' ),
					'items' => array(
						'page_contact',
					),
				),
			),
		);

		/**
		 * Filters WeCodeArt array of starter content.
		 *
		 * @param array $starter_content Array of starter content.
		 */
		return apply_filters( 'wecodeart/support/starter_content', $starter_content );
	}

	/**
	 * Load Translations
	 *
	 * @since	4.0.2
	 * @version 4.0.2
	 *
	 * @return 	void
	 */
	public function load_translations() {
		// Loads wp-content/languages/themes/wecodeart-it_IT.mo.
		load_theme_textdomain(
			'wecodeart',
			trailingslashit( WP_LANG_DIR ) . 'themes' 
		);

		// Loads wp-content/themes/child-theme-name/languages/it_IT.mo.
		load_theme_textdomain(
			'wecodeart',
			trailingslashit( wecodeart_config( 'paths' )['child'] ) . wecodeart_config( 'directories' )['languages']
		);

		// Loads wp-content/themes/wecodeart/languages/it_IT.mo.
		load_theme_textdomain(
			'wecodeart',
			trailingslashit( wecodeart_config( 'paths' )['directory'] ) . wecodeart_config( 'directories' )['languages']
		);
	}

	/**
	 * Register Menus
	 *
	 * @since	3.9.5
	 * @version	3.9.9
	 *
	 * @return 	void
	 */
	public function register_menus() {
		$header_modules = get_theme_mod( 'header-bar-modules', [ 'branding', 'menu', 'search' ] );

		if( in_array( 'menu', $header_modules ) ) {
			// Register New Menu
			register_nav_menus( [ 
				'primary' => esc_html__( 'Primary Menu', 'wecodeart' ) 
			] );
		}
	}

	/**
	 * Loads all registered integrations if their conditionals are met.
	 *
	 * @return void
	 */
	public function load() {
		foreach ( $this->items as $class ) {
			if ( ! $this->conditionals_are_met( $class ) ) {
				continue;
			}
			$class::get_instance()->register_hooks();
		}
	}

	/**
	 * Checks if all conditionals of a given integration are met.
	 *
	 * @param 	Integration $class The class name of the integration.
	 *
	 * @return 	bool Whether or not all conditionals of the integration are met.
	 */
	protected function conditionals_are_met( $class ) {
		$conditionals = $class::get_conditionals();
		return wecodeart_if( $conditionals );
	}

	/**
     * Determine if the given integration value exists.
     *
     * @param  string  $key
     *
     * @return bool
     */
    public function has( $key ) {
        return isset( $this->items[$key] );
    }

    /**
     * Get the specified integration value.
     *
     * @param  string  $key
     * @param  mixed   $default
     *
     * @return mixed
     */
    public function get( $key, $default = null ) {
        if ( ! isset( $this->items[$key] ) ) {
            return $default;
        }

        return apply_filters( "wecodeart/integration/get/{$key}", $this->items[$key] );
    }

	/**
     * Set a given integration value.
     *
     * @param  array|string  $key
     * @param  mixed   $value
     *
     * @return void
     */
    public function register( $key, $value = null ) {
        $this->set( $key, $value );
	}
	
    /**
     * Set a given integration value.
     *
     * @param  array|string  $key
     * @param  mixed   $value
     *
     * @return void
     */
    public function set( $key, $value = null ) {
        $keys = is_array( $key ) ? $key : [ $key => $value ];

        foreach ( $keys as $key => $value ) {
            $this->items[$key] = apply_filters( "wecodeart/integration/set/{$key}", $value );
        }
    }

    /**
     * Get all of the integration items for the application.
     *
     * @return array
     */
    public function all() {
        return $this->items;
    }

    /**
     * Determine if the given integration option exists.
     *
     * @param  string  $key
     *
     * @return bool
     */
    public function offsetExists( $key ) {
        return $this->has( $key );
    }

    /**
     * Get a integration option.
     *
     * @param  string  $key
     *
     * @return mixed
     */
    public function offsetGet( $key ) {
        return $this->get( $key );
    }

    /**
     * Set a integration option.
     *
     * @param  string  $key
     * @param  mixed  $value
     *
     * @return void
     */
    public function offsetSet( $key, $value ) {
        $this->set( $key, $value );
    }

    /**
     * Unset a integration option.
     *
     * @param  string  $key
     *
     * @return void
     */
    public function offsetUnset( $key ) {
        $this->set( $key, null );
    }
}