namespace WeCodeArt\Support;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Singleton;

/**
 * Manages the Theme Uploads.
 */
class FileSystem {
	
	use Singleton;

	/**
	 * WP FileSystem
	 *
	 * @since 	4.2.0
	 * @var 	mixed
	 */
	public	$filesystem	= null;
	public	$folder		= '';

	/**
	 * Send to contructor.
	 */
	public function init() {
		$this->set_folder();
	}

	/**
	 * Function to set folder in WordPress Filesystem.
	 *
	 * @since   4.2.0
	 *
	 * @return  bool
	 */
	public function set_folder( $folder = '' ) {
		$wp_upload_dir = wp_get_upload_dir();

		if ( ! $this->get_filesystem()->is_writable( $wp_upload_dir['basedir'] ) ) {
			return false;
		}

		$this->folder = implode( '/', array_filter( [ $wp_upload_dir['basedir'], 'wecodeart', $folder ] ) );

		wp_mkdir_p( wp_normalize_path( $this->folder ) );

		return $this;
	}

	/**
	 * Function to create file from WordPress Filesystem.
	 *
	 * @since   4.2.0
	 *
	 * @return  bool
	 */
	public function create_file( $filename, $content ) {
		// Delete old file
		$this->delete_file( $filename );

		// Create new file
		return $this->get_filesystem()->put_contents( $this->get_file_url( $filename ), $content, FS_CHMOD_FILE );
	}

	/**
	 * Function to delete file from WordPress Filesystem.
	 *
	 * @since   4.2.0
	 *
	 * @return  bool
	 */
	public function delete_file( $filename ) {
		// If no specific file, bail
		if ( $this->has_file( $filename ) ) {
			return $this->get_filesystem()->delete( $this->get_file_url( $filename ), true );
		}
	}

	/**
	 * Check if we have a file
	 *
	 * @since   4.2.0
	 *
	 * @return  bool
	 */
	public function has_file( $file ) {
		return is_file( $this->get_file_url( $file ) );
	}

	/**
	 * Get File url.
	 *
	 * @return  string File url.
	 */
	public function get_file_url( $filename, $link = false ) {
		$file = implode( '/', array_filter( [ $this->folder, $filename ] ) );
		
		if( $link ) {
			$file = str_replace( WP_CONTENT_DIR, content_url(), $file );
		}

		return $file;
	}

	/**
	 * Get the filesystem.
	 *
	 * @since 	4.2.0
	 *
	 * @return 	WP_Filesystem
	 */
	public function get_filesystem() {
		global $wp_filesystem;
		if ( ! $wp_filesystem ) {
			if ( ! function_exists( 'WP_Filesystem' ) ) {
				require_once wp_normalize_path( ABSPATH . '/wp-admin/includes/file.php' );
			}
			WP_Filesystem();
		}

		return $wp_filesystem;
	}
}