namespace WeCodeArt\Support\Markup\Inputs;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Support\Markup\Inputs\Basic;
use function WeCodeArt\Functions\get_prop;

/**
 * Standard Inputs Markup
 */
class Floating extends Basic {

    /**
     * Input's Type / Label Position.
     *
     * @since   5.0.0
     * @var     string
     */
    public $type    = 'floating';
    public $_label  = 'none';

    /**
	 * Constructor 
	 */
	public function __construct( string $type = 'floating', array $args = [] ) {
        $this->args = $args;
    }
	
	/**
	 * Create HTML Inputs
	 *
	 * @since	unknown
	 * @version	5.0.0
	 */
	public function content() {
        wecodeart( 'markup' )::wrap( 'form-floating', [
            [
                'tag' => 'div',
                'attrs' => [
                    'class' => 'form-floating',
                ]
            ]
        ], 'wecodeart_input', [ get_prop( $this->args, 'type', 'text' ), wp_parse_args( [
            '_label' => 'after'
        ], $this->args ) ] );
    }
}