namespace WeCodeArt\Support\Plugins;

defined( 'ABSPATH' ) || exit;

use WeCodeArt\Support\Plugins;
use WeCodeArt\Admin\Notifications;
use WeCodeArt\Admin\Notifications\Notification;
use WeCodeArt\Config\Traits\Singleton;
use WeCodeArt\Config\Interfaces\Integration;

/**
 * Contact Form 7 Integration
 * @see https://wordpress.org/plugins/contact-form-7/
 */
class ContactForm7 implements Integration {

	use Singleton;

	const NOTICE_ID = 'wecodeart/extension/cf7';

	/**
	 * Get Conditionals
	 *
	 * @return void
	 */
	public static function get_conditionals() {
		wecodeart( 'ifso' )->register( 'is_cf7_active', ContactForm7\Condition::class );

		return [ 'is_cf7_active' ];
	}

	/**
	 * Hooks
	 *
	 * @since   5.0.0
	 * @version	5.4.5
	 *
	 * @return  void
	 */
	public function register_hooks() {
		add_action( 'admin_notices', [ $this, 'manage_notice' ] );
	}

	/**
	 * Manage Notice
	 *
	 * @since 	5.0.0
	 * @version	6.1.7
	 */
	public function manage_notice() {
		if( class_exists( 'WCA\EXT\CF7' ) ) {
			return;
		}

		$notification = new Notification(
			sprintf( '<h3 style="margin: .5em 0;">%s</h3>', esc_html__( 'Amazing news!', 'wecodeart' ) ) .
			sprintf(
				esc_html__( 'We have an extension that integrates our theme with Contact Form 7 plugin! You can get it from %s.', 'wecodeart' ),
				sprintf(
					'<a href="%s" target="_blank">%s</a>',
					esc_url( admin_url( '/themes.php?page=wecodeart&tab=plugins' ) ),
					esc_html__( 'here', 'wecodeart' )
				)
			),
			[
				'id'			=> self::NOTICE_ID,
				'type'     		=> Notification::INFO,
				'priority' 		=> 1,
				'class'			=> 'notice is-dismissible',
				'capabilities' 	=> 'activate_plugins',
			]
		);
		
		if( get_user_option( self::NOTICE_ID ) === 'seen' ) {
			Notifications::get_instance()->remove_notification( $notification );
			set_transient( self::NOTICE_ID, true, WEEK_IN_SECONDS );
			return;
		}

		if( get_transient( self::NOTICE_ID ) === false ) {
			Notifications::get_instance()->add_notification( $notification );
		}
	}

	/**
	 * Installer
	 *
	 * @since	6.1.2
	 * @version	6.1.2
	 *
	 * @return 	array
	 */
	public function installer(): array {
		return [
			'slug' 			=> 'BicanMarianValeriu/wca-contact-form-7',
			'title' 		=> esc_html__( 'WCA: Contact Form 7', 'wecodeart' ),
			'description' 	=> Plugins::get_default_description( 'Contact Form 7', 'external' ),
			'source'		=> 'github',
			'required' 		=> [ 'contact-form-7' ],
			'more'			=> 'https://github.com/BicanMarianValeriu/wca-contact-form-7/',
			'recommended'	=> true
		];
	}
}