namespace WeCodeArt\Support\Styles\Components;

defined( 'ABSPATH' ) || exit;

use function WeCodeArt\Functions\encode_svg_data;

/**
 * Backdrop Styles
 */
class Backdrop extends Base {
	/**
     * Component's Style Deps.
     *
     * @var     array
     */
    static $deps = [ 'transition' ];

    /**
	 * Component styles.
	 *
	 * @return 	string
	 */
	public static function styles(): string {
		return <<<CSS
			.wp-backdrop {
				position: fixed;
				top: 0;
				left: 0;
				width: 100vw;
				height: 100vh;
				background-color: var(--wp--preset--color--black);
				z-index: 1040;
			}
			.wp-backdrop.fade {
				opacity: 0;
			}
			.wp-backdrop.show {
				opacity: .5;
			}
		CSS;
	}
}
