namespace WeCodeArt\Support\Styles\Components;

defined( 'ABSPATH' ) || exit();

/**
 * Module Styles
 */
class Popover extends Base {
    /**
     * Component's Style Deps.
     *
     * @var     array
     */
    static $deps = [ 'transition' ];

    /**
	 * Component styles.
	 *
	 * @return 	string
	 */
	public static function styles(): string {
        $inline = "
            .popover {
                --wp--popover--zindex: 70;
                --wp--popover--max-width: 276px;
                --wp--popover--font-size: 0.875rem;
                --wp--popover--bg: var(--wp--preset--color--white);
                --wp--popover--border-width: 1px;
                --wp--popover--border-color: var(--wp--preset--color--accent);
                --wp--popover--border-radius: 5px;
                --wp--popover--inner-border-radius: calc(5px - var(--wp--popover--border-width));
                --wp--popover--box-shadow: 0 0 5px rgba(0,0,0,.1);
                --wp--popover--header-padding-x: 1rem;
                --wp--popover--header-padding-y: 0.5rem;
                --wp--popover--header-font-size: 1rem;
                --wp--popover--header-color: inherit;
                --wp--popover--header-bg: var(--wp--preset--color--accent);
                --wp--popover--body-padding-x: 1rem;
                --wp--popover--body-padding-y: 1rem;
                --wp--popover--body-color: var(--wp--preset--color--dark);
                --wp--popover--arrow-width: 1rem;
                --wp--popover--arrow-height: 0.5rem;
                --wp--popover--arrow-border: var(--wp--popover--border-color);
                z-index: var(--wp--popover--zindex);
                display: block;
                max-width: var(--wp--popover--max-width);
                font-family: var(--wp--preset--font-family--base);
                font-size: var(--wp--popover--font-size);
                font-style: normal;
                font-weight: 400;
                text-align: left;
                text-align: start;
                text-decoration: none;
                text-shadow: none;
                text-transform: none;
                word-break: normal;
                white-space: normal;
                word-spacing: normal;
                letter-spacing: normal;
                line-height: 1.5;
                line-break: auto;
                word-wrap: break-word;
                background-color: var(--wp--popover--bg);
                background-clip: padding-box;
                border: var(--wp--popover--border-width) solid var(--wp--popover--border-color);
                border-radius: var(--wp--popover--border-radius);
                box-shadow: var(--wp--popover--box-shadow);
            }
            
            .popover .popover-arrow {
                display: block;
                width: var(--wp--popover--arrow-width);
                height: var(--wp--popover--arrow-height);
            }
            
            .popover .popover-arrow::before,
            .popover .popover-arrow::after {
                content: '';
                position: absolute;
                display: block;
                border-color: transparent;
                border-style: solid;
                border-width: 0;
            }
            
            /* Top */
            .bs-popover-auto[data-popper-placement^=top] > .popover-arrow {
                bottom: calc(-1 * (var(--wp--popover--arrow-height)) - var(--wp--popover--border-width));
            }
            
            .bs-popover-auto[data-popper-placement^=top] > .popover-arrow::before,
            .bs-popover-auto[data-popper-placement^=top] > .popover-arrow::after {
                border-width: var(--wp--popover--arrow-height) calc(var(--wp--popover--arrow-width) * .5) 0;
            }
            
            .bs-popover-auto[data-popper-placement^=top] > .popover-arrow::before {
                bottom: 0;
                border-top-color: var(--wp--popover--arrow-border);
            }
            
            .bs-popover-auto[data-popper-placement^=top] > .popover-arrow::after {
                bottom: var(--wp--popover--border-width);
                border-top-color: var(--wp--popover--bg);
            }
            
            /* Right */
            .bs-popover-auto[data-popper-placement^=right] > .popover-arrow {
                left: calc(-1 * (var(--wp--popover--arrow-height)) - var(--wp--popover--border-width));
                width: var(--wp--popover--arrow-height);
                height: var(--wp--popover--arrow-width);
            }
            
            .bs-popover-auto[data-popper-placement^=right] > .popover-arrow::before,
            .bs-popover-auto[data-popper-placement^=right] > .popover-arrow::after {
                border-width: calc(var(--wp--popover--arrow-width) * .5) var(--wp--popover--arrow-height) calc(var(--wp--popover--arrow-width) * .5) 0;
            }
            
            .bs-popover-auto[data-popper-placement^=right] > .popover-arrow::before {
                left: 0;
                border-right-color: var(--wp--popover--arrow-border);
            }
            
            .bs-popover-auto[data-popper-placement^=right] > .popover-arrow::after {
                left: var(--wp--popover--border-width);
                border-right-color: var(--wp--popover--bg);
            }
            
            /* Bottom */
            .bs-popover-auto[data-popper-placement^=bottom] > .popover-arrow {
                top: calc(-1 * (var(--wp--popover--arrow-height)) - var(--wp--popover--border-width));
            }
            
            .bs-popover-auto[data-popper-placement^=bottom] > .popover-arrow::before,
            .bs-popover-auto[data-popper-placement^=bottom] > .popover-arrow::after {
                border-width: 0 calc(var(--wp--popover--arrow-width) * .5) var(--wp--popover--arrow-height);
            }
            
            .bs-popover-auto[data-popper-placement^=bottom] > .popover-arrow::before {
                top: 0;
                border-bottom-color: var(--wp--popover--arrow-border);
            }
            
            .bs-popover-auto[data-popper-placement^=bottom] > .popover-arrow::after {
                top: var(--wp--popover--border-width);
                border-bottom-color: var(--wp--popover--arrow-border);
            }
            
            .bs-popover-auto[data-popper-placement^=bottom] > .popover-header::before {
                content: '';
                position: absolute;
                top: 0;
                left: 50%;
                display: block;
                width: var(--wp--popover--arrow-width);
                margin-left: calc(-.5 * var(--wp--popover--arrow-width));
                border-bottom: var(--wp--popover--border-width) solid var(--wp--popover--header-bg);
            }
            
            /* Left */
            .bs-popover-auto[data-popper-placement^=left] > .popover-arrow {
                right: calc(-1 * (var(--wp--popover--arrow-height)) - var(--wp--popover--border-width));
                width: var(--wp--popover--arrow-height);
                height: var(--wp--popover--arrow-width);
            }
            
            .bs-popover-auto[data-popper-placement^=left] > .popover-arrow::before,
            .bs-popover-auto[data-popper-placement^=left] > .popover-arrow::after {
                border-width: calc(var(--wp--popover--arrow-width) * .5) 0 calc(var(--wp--popover--arrow-width) * .5) var(--wp--popover--arrow-height);
            }
            
            .bs-popover-auto[data-popper-placement^=left] > .popover-arrow::before {
                right: 0;
                border-left-color: var(--wp--popover--arrow-border);
            }
            
            .bs-popover-auto[data-popper-placement^=left] > .popover-arrow::after {
                right: var(--wp--popover--border-width);
                border-left-color: var(--wp--popover--bg);
            }
            
            .popover-header {
                padding: var(--wp--popover--header-padding-y) var(--wp--popover--header-padding-x);
                margin: 0;
                font-size: var(--wp--popover--header-font-size);
                color: var(--wp--popover--header-color);
                background-color: var(--wp--popover--header-bg);
                border-bottom: var(--wp--popover--border-width) solid var(--wp--popover--border-color);
                border-top-left-radius: var(--wp--popover--inner-border-radius);
                border-top-right-radius: var(--wp--popover--inner-border-radius)
            }
            
            .popover-header:empty {
                display: none
            }
            
            .popover-body {
                padding: var(--wp--popover--body-padding-y) var(--wp--popover--body-padding-x);
                color: var(--wp--popover--body-color)
            }
        ";

        return $inline;
	}
}