namespace WeCodeArt\Support\Styles\Property;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Support\Styles;

/**
 * Output for CSS properties.
 */
class Background extends Styles\Property {
	/**
	 * Modifies the value.
	 *
	 * @access protected
	 */
	protected function process_value() {
		$processed = '';

		if ( $this->value ) {
			if( filter_var( $this->value, FILTER_VALIDATE_URL ) ) {
				$processed = sprintf( 'url(%s)', esc_url( $this->value ) );
			} else {
				$processed = filter_var( $this->value, FILTER_SANITIZE_STRING );
			}
		}
		
		$this->value = $processed;
	}
}