namespace WeCodeArt\Support\Styles\Property;

defined( 'ABSPATH' ) || exit();

use WeCodeArt\Support\Styles;

/**
 * Output for CSS properties.
 */
class Font extends Styles\Property {
	/**
	 * Modifies the value.
	 *
	 * @access protected
	 */
	protected function process_value() {
		$processed = $this->value;

		if ( is_array( $processed ) && isset( $processed['font-family'] ) ) {
			$processed = $processed['font-family'];
		}

		// Make sure the value is a string, if not, then early exit.
		if ( ! is_string( $processed ) ) {
			return;
		}

		// Hack for standard fonts.
		$processed = str_replace( '&quot;', '"', $processed );

		// Add double quotes if needed.
		if ( false !== strpos( $processed, ' ' ) && false === strpos( $processed, '"' ) ) {
			$processed = '"' . $processed . '"';
		}

		$this->value = html_entity_decode( $processed, ENT_QUOTES );
	}
}