defined( 'ABSPATH' ) || exit;

wecodeart( 'styles' )->Utilities->load( [ 'mb-3' ] );

// Escaping attrs is not necessary with wecodeart_input()
// Is already done inside the function via esc_attr()

/**
 * @param   string  $action Contains the form action url
 * @param   array   $args   Contains the form args
 */
?>
<form action="<?php echo esc_url( $action ); ?>" method="post">
    <?php
    
        // WP Filter
        echo apply_filters( 'login_form_top', '', $args );
    
    ?>
    <?php
            
        wecodeart_input( 'floating', [
            'type'  => 'text',
            'label' => $args['label_username'],
            'attrs' => [
                'id'        => $args['id_username'],
                'name'      => 'log',
                'value'     => $args['value_username'],
                'size'      => 20,
                'required'  => true,
                'class'     => 'form-control mb-3',
                'placeholder' => ' ',
            ]
        ] );
                
        wecodeart_input( 'floating', [
            'type'  => 'password',
            'label' => $args['label_password'],
            'attrs' => [
                'id'        => $args['id_password'],
                'name'      => 'pwd',
                'size'      => 20,
                'required'  => true,
                'class'     => 'form-control mb-3',
                'placeholder' => ' ',
            ]
        ] );
    
        // WP Filter
        echo apply_filters( 'login_form_middle', '', $args );
    
        if( $args['remember'] ) :
    
        wecodeart_input( 'toggle', [
            'type'  => 'checkbox',
            'label' => $args['label_remember'],
            'attrs' => [
                'id'        => $args['id_remember'],
                'name'      => 'rememberme',
                'value'     => 'forever',
                'class'     => 'form-switch mb-3',
                'checked'   => (bool) $args['value_remember']
            ]
        ] );
    
    ?><?php endif; ?>
    <div class="wp-block-button wp-block-button--login mb-3">
    <?php

        wecodeart_input( 'submit', [
            'attrs' => [
                'name'  => false,
                'id'    => 'wp-submit',
                'value' => $args['label_log_in'],
                'class' => 'wp-block-button__link',
            ]
        ] );

        wecodeart_input( 'hidden', [
            'attrs' => [
                'id'    => false,
                'name'  => 'redirect_to',
                'value' => esc_url( $args['redirect'] )
            ]
        ] );

    ?>
    </div>
    <?php
        
        // WP Filter
        echo apply_filters( 'login_form_bottom', '', $args );
        
    ?>
</form>
		