<?php if ( ! defined( 'ABSPATH' ) ) exit(); // Exit if accessed directly.
// Use
use WeCodeArt\Utilities\Markup\SVG;
/**
 * WeCodeArt Framework.
 *
 * WARNING: This file is part of the core WeCodeArt Framework. DO NOT edit this file under any circumstances.
 * Please do all modifications in the form of a child theme.
 *
 * @package 	WeCodeArt Framework
 * @subpackage  Header Branding HTML
 * @since	 	v3.0.5
 * @version    	v3.5
 */
?>
<div id="bar-branding" class="header-bar__branding cell auto large-shrink">
	<div class="grid-x grid-padding-x align-middle">
		<div class="cell auto">	
			<?php 
				if ( has_custom_logo() ) the_custom_logo();
				if ( is_front_page() ) : ?>	
					<h1 class="site-title"><a href="<?php echo esc_url( home_url( '/' ) ); ?>" rel="home"><?php bloginfo( 'name' ); ?></a></h1>		
				<?php else : ?>
				<p class="site-title"><a href="<?php echo esc_url( home_url( '/' ) ); ?>" rel="home"><?php bloginfo( 'name' ); ?></a></p>
			<?php 
				endif; 						
				$description = get_bloginfo( 'description', 'display' );
				if ( $description || is_customize_preview() ) : ?>
					<p class="site-description"><?php echo $description; ?></p>
			<?php 
				endif; 
			?>
		</div>
		<?php 
			$mod = get_theme_mod( 'header-bar-modules' );
			if ( in_array( 'search', $mod ) ) {
			// Display a search toggle on small devices ?>
				<div class="cell shrink hide-for-large" data-responsive-toggle="bar-search" data-hide-for="large">
					<button class="button small secondary" type="button" data-toggle="bar-search">
						<span class="show-for-sr"><?php _e( 'Search', 'wecodeart' ); ?></span>
						<?php SVG::render( 'icon--search' ); ?>
					</button>
				</div>
			<?php }
			if ( in_array( 'menu', $mod ) ) { 
			// Display a menu toggle on small devices ?>
				<div class="cell shrink hide-for-large" data-responsive-toggle="bar-menu" data-hide-for="large">
					<button class="button small secondary" type="button" data-toggle="bar-menu">
						<span class="show-for-sr"><?php _e( 'Primary Menu', 'wecodeart' ); ?></span>
						<?php SVG::render( 'icon--bars' ); ?>
					</button>
				</div>
			<?php }
		?>
	</div>
</div>
<!-- /bar-branding -->